<?php

function new_text($mantenimiento) {
	$logo = 'data:image/svg+xml;base64,';

		$mantenimiento ='
		<main class="text-align-center-xs">
			<div class="contenido">
				<img src="'.$logo.'">
				<h1>Estamos en mantenimiento</h1>
				<p>Pronto volveremos con nuevas mejoras en nuestro sitio.</p>
			</div>
		</main>
		';    

    return $mantenimiento;
}
add_filter('wpmm_text', 'new_text');

function wpmm_add_custom_css() {
	?>
	<style>
		<?php echo get_field('modo_mantencion_style', 'options'); ?>

		.wrap {
			width: 100%;
			margin: 0;
			display: flex;
			justify-content: center;
			align-items: center;
			height: 100vh;
			width: 100vw;
		}
		.wrap h2 {
			margin: 0
		}

		.wrap h1 {
			margin-top: .75em;
			margin-bottom: .75em;
		}

		img {
			display:inline-block;
			width: 120px;
		}

		main {
			display:flex;
			align-items: center;
			justify-content: center;
			width: calc(100vw - 50px);
			height: calc(100vh - 50px);
			background: #fff;
			padding: 50px;
		}
	</style>
	<?php
}
add_action('wpmm_head', 'wpmm_add_custom_css');

